module.exports = (function() {
    'use strict';

    const fs = require('fs');
    const path = require('path');
    const chalk = require('chalk');
    const Type = require('./Type');

    /**
     * Default Adapter for the AppData storage engine
     * @class Adapter
     */
    function Adapter() {
        this.type = Type.Adapter;
        this.options = {};
        this.engine = null;
    }

    /**
     * Default: throw error on write if not overwritten
     * @memberof Adapter
     * @return {Error}
     */
    Adapter.prototype.read = function() {
        return new Error('Read method on Adapter not defined!');
    };

    /**
     * Default: throw error on write if not overwritten
     * @memberof Adapter
     * @return {Error}
     */
    Adapter.prototype.write = function() {
        return new Error('Write method on Adapter not defined!');
    };

    return Adapter;
})();
