/** @memberof Transform **/
module.exports = (function() {
    'use strict';

    const utils = require('util');
    const Transform = require('./../Transform');

    /**
     * Default transform with simple io principe
     * @private
     * @augments Transform
     * @constructor TransformDefault
     */
    function TransformDefault() {
        this.type = this.type + '_DEFAULT';
    }

    utils.inherits(Transform, TransformDefault);

    /**
     * Returns the input
     * @override
     * @memberof TransformDefault
     * @param {*} input - Input to return
     */
    TransformDefault.prototype.parse = input => {
        return input;
    };

    /**
     * Returns the input
     * @override
     * @memberof TransformDefault
     * @param {Object} input - Input to return
     */
    TransformDefault.prototype.convert = input => {
        return input;
    };

    return new TransformDefault();
})();
