module.exports = (function() {
    'use strict';

    const utils = require('util');
    const Transform = require('./../Transform');

    /**
     * JSON Transformer for Adapter
     * @private
     * @augments Transform
     * @constructor TransformJSON
     */
    function TransformJSON() {
        this.type = this.type + '_JSON';
    }

    utils.inherits(Transform, TransformJSON);

    /**
     * Parse a JSON input to a JavaScript Object
     * @override
     * @memberof TransformJSON
     * @param {Object} input - Input to parse
     */
    TransformJSON.prototype.parse = input => {
        return JSON.parse(input);
    };

    /**
     * Converts a JavaScript Object to a JSON string
     * @override
     * @memberof TransformJSON
     * @param {Object} input - Input to convert
     */
    TransformJSON.prototype.convert = input => {
        return JSON.stringify(input);
    };

    return new TransformJSON();
})();
