/**
 * Basic factory set to contain bootstrap templates for classes
 * @module util/factory
 */
module.exports = (function() {
    'use strict';

    const shortid = require('shortid');
    const pkg = require('./../../package.json');

    /**
     * @exports util/factory
     */
    return {
        /**
         * Factory for a basic store structure
         * @alias module:util/factory.Store
         * @type {Object}
         */
        Store: {
            _id: shortid.generate(),
            version: pkg.version,
            created: new Date(),
            data: {}
        }
    };
})();
